import React from 'react';
import { DropdownMenu, DropdownItem } from 'reactstrap';

const filterOptions = {
  no_effect: "none",
  contrast: "contrast:100",
  blur: "blur:100",
  pixelate: "pixelate:30",
  auto_brightness: "auto_brightness",
  auto_saturation: "auto_saturation",
  sepia: "sepia:80",
  vignette: "vignette:20",
  black_white: "blackwhite"
}

const ImageFilters = (props) => {
  return (
    <div>
      <DropdownMenu value={props.selectedImageFilter || "none"} onChange={props.handleFilterChange}>

      FILTERS:
      <select value={props.selectedImageFilter || "none"} onChange={props.handleFilterChange}>
        {
          Object.keys(filterOptions).map(
          (k) =>
          <DropdownItem>
            <option key={k} value={filterOptions[k]}>{k}</option>
          </DropdownItem>
          )
        }
      </select>
            </DropdownMenu>
    </div>
  );
};


export default ImageFilters
