import React, { Component } from 'react';

class ImageFilters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedFilter: ''
    };
    this.filterOptions = {
      light_contrast: "contrast(0.35)",
      heavy_contrast: "contrast(0.65)",
      light_blur: "blur(15)",
      heavy_blur: "blur(40)",
      sepia: "contrast(1.4) sepia(1)"
    }
  }

  handleChange = (e) => {
    debugger
    this.setState({ selectedFilter: e.target.value });
  }

  render() {
    return (
      <div>
        <select onChange={this.handleChange}>
          {Object.keys(this.filterOptions).map((k) =>
            <option key={k} value={this.filterOptions[k]}>{k}</option>)}
        </select>
      </div>
    )
  }

}


export default ImageFilters;
