import React, { Component } from 'react';

class ImageFilters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedFilter: ''
    };
  }

  filterOptions = () => {
    return {
      light_contrast: "contrast(0.35)",
      heavy_contrast: "contrast(0.65)",
      light_blur: "blur(15)",
      heavy_blur: "blur(40)",
      sepia: "contrast(1.4) sepia(1)"
    }
  }

  render() {
    debugger
    return (
      <div>
        <select>
         {Object.entries(this.filterOptions()).map((k, v) =>
           <option value={v}>{k}</option>)}
        </select>
      </div>
    )
  }

}


export default ImageFilters;
