import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchImages } from '../../actions/imageActions'
import ImageList from './ImageList'
import ImageContainer from './ImageContainer'

class ImagesContainer extends Component {

  componentDidMount() {
    this.props.fetchImages(this.props.user)
  }

  handleLoadingImages = () => {
    if(this.props.loading) {
      return (
        <div>
          <p>Loading Images...</p>
        </div>
      )
    } else {
      return <ImageList images={this.props.images} />
    }
  }
   handleLoadingImage = () => {
     if(this.props.loading) {
       return (
         <div>
           <p>Click on an image to edit.</p>
         </div>
       )
     } else {
       return (
         <ImageContainer image={this.state.currentImage}/>
       )
     }
   }


  render() {
    const image = this.state.currentImage

    return (
      <div className="container">
        <div className="row">
          <div className="col-md-2">
            {this.handleLoadingImages()}
          </div>
          <div className="col">
            { image !== undefined ? <ImageContainer image={image}/> : ""}
          </div>
        </div>
      </div>
    )
  }
}

//inline (mapStateToProps, mapDispatchToProps)
export default connect(
  state => ({
    images: state.imagesReducer.images,
    user: state.loginReducer.user,
    currentImage: state.imagesReducer.currentImage
  }),
{ fetchImages })(ImagesContainer);
