import React, { Component } from 'react';
import { connect } from 'react-redux';
import { removeImage } from '../../actions/imageActions';

class ImagePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      image: ''
      }
    };

  componentDidMount() {
    let image = this.props.match.params
    this.setState({
      image
    });
  }

  handleRemove = () => {
    this.props.removeImage(this.props.match.params)
  }

  render() {
    debugger
    let image = this.props.images.filter(image => image.id === this.state.image.id)
    return (
      <li>
        {console.log(this.props.images)}

        <button onClick={handleRemove => this.handleRemove()}>Remove Image</button>
      </li>
    );
  }
};




export default connect(
  state => ({
    images: state.imagesReducer.images,
    user: state.loginReducer.user
  }),
  { removeImage })(ImagePage);
