import React, { Component } from 'react';
import { connect } from 'react-redux';
import { removeImage } from '../../actions/imageActions';

class ImagePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      imageId: this.props.match.params.id,
      selectedFilter: {}
    };
  };

  componentDidMount() {
    <Filters />
    {/*ctx.filter = 'contrast(1.4) sepia(1) drop-shadow(9px 9px 2px #e81)';*/}
    const canvas = this.refs.canvas
    const ctx = canvas.getContext("2d")
    const img = this.refs.image
    img.onload = () => {
      ctx.drawImage(img, 0, 0, 800, 600)
      ctx.font = "40px Courier"
      ctx.fillText("Hello!", 350, 75)
    }
  }

  handleRemove = () => {
    this.props.removeImage(this.props.match.params.id, this.props.user.id, this.props.history)
  }

  render() {
    let image = this.props.images.filter(image => image.id.toString() === this.state.imageId)[0]
    return (
      <div>
        <canvas ref="canvas" width={800} height={600}>
        {/*unable to use stateless functional component because of refs*/}
        <img ref="image"
          className='hidden'
          key={image.id}
          src={image.image_url}
          alt={image.id}
        />
      </canvas>
        <button onClick={handleRemove => this.handleRemove()}>Remove Image</button>
      </div>
    );
  }
};

export default connect(
  state => ({
    images: state.imagesReducer.images,
    user: state.loginReducer.user
  }),
  { removeImage })(ImagePage);
