import React, { Component } from 'react';
import { connect } from 'react-redux';
import { login } from '../../actions/loginActions'

class LoginForm extends Component {

  constructor(props) {
    super(props);
    this.state = {
      user: {
        email: '',
        password: ''
      }
    };
  }

  handleSubmit(e) {
    e.preventDefault();
    this.props.login(this.state.email, this.state.password, this.props.history);
  }

  render() {
    let user = {...this.state.user}
    return (
      <form name="loginForm" onSubmit={(event) => this.handleSubmit(event)}>
        <div className="form-group-collection">
          <div className="form-group">
            <label>Email:</label>
            <input type="email" name="email" onChange={user.email = e.target.value} value={this.state.email}/>
          </div>

          <div className="form-group">
            <label>Password:</label>
            <input type="password" name="password" onChange={user.password = e.target.value} value={this.state.password}/>
          </div>
        </div>
        {this.setState({user})}
        <input type="submit" value="Login" />
      </form>
    )
  }
}

// const mapStateToProps = state => ({ user: {
//   email: state.email,
//   password: state.password
//   }
// })

// const mapDispatchToProps = dispatch => ({
//   login: user => dispatch({type: 'GET_USER', user})
// })
//
// export default connect(mapStateToProps, mapDispatchToProps)(LoginForm);
// export default connect(mapStateToProps, { login })(LoginForm);

export default connect(state => ({ user: state.user }), { login })(LoginForm);
