import React, { Component } from 'react';

class ImageFilters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedFilter: ''
    };
    this.filterOptions = {
      light_contrast: "contrast(0.35)",
      heavy_contrast: "contrast(0.65)",
      light_blur: "blur(15)",
      heavy_blur: "blur(40)",
      sepia: "contrast(1.4) sepia(1)"
    }
  }

  render() {
    return (
      <div>
        <select>
          {Object.keys(this.filterOptions).map((k, v) =>
            <option key={k}>{k}</option>)}
        </select>
      </div>
    )
  }

}


export default ImageFilters;
