const imagesReducer = (state = { images: [], loading: false }, action) => {
  switch(action.type) {
    case 'LOADING_IMAGES':
      return {
        ...state,
        images: [...state.images],
        loading: true
      }
    case 'DISPLAY_IMAGES':
      return {
        ...state,
        images: action.images,
        loading: false
      }
    case 'UPLOAD_IMAGES':
      return {
        ...state,
        images: [...state, action.image],
        loading: false
      }
    default:
      return state;
  }
}

export default imagesReducer;
