import React, { Component } from 'react';

class ImageFilters extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedImageFilter: ''
    };

    this.filterOptions = {
      none: null,
      contrast: "contrast:100",
      blur: "blur:100",
      pixelate: "pixelate:30",
      auto_brightness: "auto_brightness",
      auto_saturation: "auto_saturation",
      sepia: "sepia:80",
      vignette: "vignette:20"
    }
  }

  render() {
    console.log(this.props);
    return (
      <div>
        <select onChange={this.props.handleFilterChange}>
          {Object.keys(this.filterOptions).map((k) =>
            <option key={k} value={this.filterOptions[k]}>{k}</option>)}
        </select>
      </div>
    )
  }

}


export default ImageFilters
