import React from 'react';
import { logout } from '../../actions/loginActions'
import { connect } from 'react-redux';


const LogoutButton = () => {

  const handleLogout = (e) => {
    e.preventDefault();
    logout();
  }

  return (
    <button
      onClick={e => handleLogout(e)}>
      Log out
    </button>
  )
};

export default connect(null, { logout })(LogoutButton);
