import React from 'react';
import { UncontrolledDropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';

const filterOptions = {
  no_effect: "none",
  contrast: "contrast:100",
  blur: "blur:100",
  pixelate: "pixelate:30",
  auto_brightness: "auto_brightness",
  auto_saturation: "auto_saturation",
  sepia: "sepia:80",
  vignette: "vignette:20",
  black_white: "blackwhite"
}

export default function ImageFilters () {
  return (
    <UncontrolledDropdown setActiveFromChild>
      <DropdownToggle tag="a" className="nav-link" caret>
        FILTERS
      </DropdownToggle>
      <DropdownMenu onSelect={props.selectedImageFilter || "none"} onChange={props.handleFilterChange}>
        {
          Object.keys(filterOptions).map(
          (k) =>
          <DropdownItem key={k} value={filterOptions[k]}>{k}</DropdownItem>
          )
        }
      </DropdownMenu>
    </UncontrolledDropdown>
  );
}
