import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Image, Transformation, CloudinaryContext} from 'cloudinary-react';
import ImageContainer from './ImageContainer'
import { currentImage } from '../../actions/imageActions'

class ImageList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentImage: null
    };
  };


  handleClick = (image) => {
    this.setState({currentImage: image });
    this.props.currentImage(image)

    debugger
  }

   render() {
     return (
       this.props.images.map(image =>
         <div key={image.id}>
             <CloudinaryContext cloudName="df8jfhyew" >
               <Image className='img-thumbnail' publicId={image.image_url} onClick={() => this.handleClick(image)}>
                 <Transformation effect={this.state.currentImage.transformations} />
               </Image>
             </CloudinaryContext>
         </div>
       )
     )
   }

}

export default connect(
  state => ({
    currentImage: state.imagesReducer.currentImage
  }),{ currentImage })(ImageList);
