import React, { Component } from 'react';
import { connect } from 'react-redux';
import { login } from '../../actions/loginActions'

class LoginForm extends Component {

  constructor(props) {
    super(props);
    this.state = {
      user: {
        email: '',
        password: ''
      }
    };
  }

  handleSubmit(e) {
    e.preventDefault();
    let user = this.state.user
    this.props.login(user, this.props.history);
  }

  handleError(error) {
    let errors = Object.entries(error).map(([key,value])=>{
      return (
        <div>
          <li>{key} : {value.toString()}</li>
        </div>
      );
    })
  }

  render() {
    let loginErrors = this.props.user.user
    // let passwordError = this.props.user.user
    return (
      <form name="loginForm" onSubmit={(event) => this.handleSubmit(event)}>
        <div className="form-group-collection">
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              onChange={e => this.setState({
                user: { ...this.state.user, email: e.target.value} })}
              value={this.state.email}/>
          </div>

          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              onChange={e => this.setState({
                user: { ...this.state.user, password: e.target.value} })}
              value={this.state.password}/>
          </div>
        </div>
        <input type="submit" value="Login" />
        <div className="message">
           { loginErrors &&
             <div>{
             Object.entries(loginErrors).map((key,value)=>{
               return (
                 <div key={key}>
                   {key} : {value.toString()}
                 </div>
               );
             })
           } </div>
         }
        </div>
      </form>
    )
  }
}

export default connect(
  state => ({
    user: state.loginReducer.user
  }), { login })(LoginForm);
